.libraryExtension {
    position: absolute;
    z-index: 198;
    width: 20em !important;
    top: 105px !important;
  background-color: var(--theme-color)!important;
}

#aud_right_col .libraryExtension {
    top: -130px !important;
}

.libraryExtension .mbcContainer, 
/* don't show on video pages  */
.av-dp-container .libraryExtension {
    display: none !important;
}

#buybox .a-expander-content,
.libraryExtension:hover .mbcContainer,
.libraryExtension:focus .mbcContainer,
.libraryExtension:active .mbcContainer {
    display: block !important;
}
.libraryExtension #libraryUserMessage {
    display: none !important;
}

