/* ==UserStyle==
@name           Carb Manager Dark
@namespace      https://github.com/Brandan/UserStyles/raw/main/carbmanager.com.user.css
@author         Brandan E. Lloyd
@description    `Modifications to Carb Manager's style to enable a dark mode`
@version        20230822
@license        GPL
==/UserStyle== */

@-moz-document domain("carbmanager.com") {

    :root {
        --cm-color-gray800: #363636;
        --cm-color-gray750: #393939;
        --cm-color-gray700: #414141;
        --cm-color-gray650: #424242;
        --cm-color-gray600: #444;
        --cm-color-gray400: #666;
        --cm-color-gray300: #999;
        --cm-color-gray200: #aaa;
        --cm-color-gray150: #bbb;
        --cm-color-gray100: #ccc;
        --cm-color-gray75: #ddd;
        --cm-color-gray50: #eee;
    }

    .flat-card {
      --flat-card-bg:#414141 !important;
      --flat-card-border-hover: var(--text-link) !important;
    }

    .confirm-dialog[data-v-3d1a8d64] {
      background-color: var(--cm-color-gray700) !important;
    }

    .video.video--loading {
        background: var(--cm-color-gray600) !important;
    }

    .video-loader__video {
      filter: invert(0.7399999999) !important
    }

    .cm-header--scrolled {
        --header-scrolled-bg: var(--cm-color-gray800) !important;
    }
    .food-search {
        --food-search-result-title-color: var(--cm-color-gray100);
    }
    .nutritional-facts {
        --nutritional-facts-heading-color: var(--cm-color-gray75) !important;
        --nutritional-facts-default-value-color: var(--cm-color-gray75) !important;
    }

    .app--with-background {
        background-image: none;
    }

    .account-base__inner {
        background: var(--cm-color-gray400) !important;
    }
    .page-header {
      background-color: var(--cm-color-gray400);
      border-radius: 1.25rem;
      box-shadow: var(--box-shadow-default);
      padding: var(--lh-2);
      padding: 0;
    }

    .app-sidebar {
      --nav-user-profile-link-color: var(--cm-color-gray300) !important;
      --nav-hover-background: var(--cm-color-gray150) !important;
      --nav-focus-background: var(--cm-color-gray150) !important;
      --nav-active-background: var(--cm-color-gray300) !important;
      --nav-active-hover-background: var(--cm-color-gray150) !important;
      --nav-active-color: var(--cm-color-green) !important;
    }

    .app-sidebar__header, .progress__title--darkened {
      color: var(--cm-color-gray800) !important;
    }
    .q-select__dropdown-icon {
      background: url("data:image/svg+xml;charset=utf-8,%3Csvg viewBox='0 0 11 6' fill='none' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M5.5 4.631L10.322 0 11 .718 5.5 6 0 .718.678 0 5.5 4.631z' fill='%23eeeeee'/%3E%3C/svg%3E") no-repeat;
    }

    .cm-more-menu__option-cta,
    .cm-more-menu__inner,
    .q-card,
    .q-date,
    .q-drawer,
    .q-menu,
    .q-header {
      background: #666;
      color: #fff;
    }

    .cm-modal-footer, .cm-modal-bar, .cm-modal-bar__toolbar {
       background-color: var(--cm-color-gray400) !important;   
    }

    .connect-card,
    .daily-card__content,
    .header{
       background-color: var(--cm-color-gray400) !important;
    }

    .planned-item,
    .food-finder-autocomplete__result-container {
       background-color: var(--cm-color-gray800) !important;
    }

    .food-nutrition__label,
    .recipe-details__value,
    .macro-progress__label,
    .food-finder-autocomplete__container input,
    .user-edits-notice,
    .daily-steps-info__value,
    input { 
        color: var(--cm-color-gray50)!important;
    }

    .food-verification-link {
      background: var(--cm-color-gray75) !important;
    }

    .fasting-banner, #onetrust-banner-sdk {
        display:none;
    }

    .recipe-card-slider__shadow-after,
    .swiper-navigation__shadow-after {
      background: -webkit-gradient(linear,left top,right top,from(rgba(33,33,33,0)),color-stop(rgba(33,33,33,.25)),color-stop(rgba(33,33,33,.5)),color-stop(rgba(33,33,33,.75)),to(var(--cm-color-gray700))) !important;
      background: linear-gradient(90deg,rgba(33,33,33,0) 0,rgba(33,33,33,.25) var(--lh),rgba(33,33,33,.5) calc(var(--lh) * 1.5),rgba(33,33,33,.75) var(--lh-2),var(--cm-color-gray700) calc(var(--lh) * 2.5)) !important;
    }

    .recipe-card-slider__shadow-before,
    .swiper-navigation__shadow-before {
      background: -webkit-gradient(linear,left top,right top,from(rgba(33,33,33,0)),color-stop(rgba(33,33,33,.25)),color-stop(rgba(33,33,33,.5)),color-stop(rgba(33,33,33,.75)),to(var(--cm-color-gray700))) !important;
      background: linear-gradient(270deg,rgba(33,33,33,0) 0,rgba(33,33,33,.25) var(--lh),rgba(33,33,33,.5) calc(var(--lh) * 1.5),rgba(33,33,33,.75) var(--lh-2),var(--cm-color-gray700) calc(var(--lh) * 2.5)) !important;
    }

    .goals-base,
    .goal,
    .goal-info,
    .settings-container {
       background-color: var(--cm-color-gray600) !important;
    }

    .progress__title {
        color: var(--cm-color-gray800) !important;
    }

    .pie-chart .ct-label, 
    .progress__title--inverted {
        color: white !important;
        fill: white !important;
    }

    .goal-info__name, .fixed-toolbar-nav {
        background-color: var(--cm-color-gray800) !important;
    }

    aside.daily-log__page-sidebar button, aside.daily-log__page-sidebar .daily-log-button, .cm-more-menu {
        border: solid var(--cm-color-green-dark) 1px !important;
    }

    .message-list__item[data-v-201d6c0d]{
        display:none;
    }

    .planned-item__hint {
      font-size: var(--cm-text-xxs) !important;
    }
}

/* ==UserStyle==
@name           ZendDesk Dark
@namespace      https://github.com/Brandan/UserStyles/raw/main/ZendDeskDark.user.css
@author         Brandan E. Lloyd
@description    `Modifications to ZenDesk styls to enable a dark mode`
@version        20220729.3
@license        GPL
==/UserStyle== */

@-moz-document 
  domain("zendesk.com"),
  domain("zdusercontent.com") {
    :root {
        --theme-color: #1a1a1a;
        --selected-color: #334;
        --text-color: #ccc;
        --border-color: rgba(204, 204, 204, .2);
        --accent-color: #3397fa;
        --base-background: #27292b;
        --open-background: rgb(227, 79, 50);
        --closed-background: rgb(216, 220, 222);
        --new-background: rgb(255, 176, 87);
        --pending-background: rgb(48, 145, 236);
        --solved-background: rgb(135, 146, 157);
    }

    html,
    body,
    div,
    object,
    iframe,
    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    input,
    p,
    blockquote,
    button,
    button *,
    pre,
    abbr,
    address,
    cite,
    code,
    del,
    dfn,
    em,
    img,
    ins,
    kbd,
    q,
    samp,
    small,
    strong,
    sub,
    sup,
    var,
    b,
    i,
    dl,
    dt,
    dd,
    ol,
    ul,
    li,
    fieldset,
    form,
    label,
    legend,
    table,
    caption,
    tbody,
    tfoot,
    thead,
    td,
    th,
    tr,
    article,
    aside,
    canvas,
    details,
    figcaption,
    figure,
    footer,
    header,
    hgroup,
    main,
    menu,
    nav,
    section,
    summary,
    time,
    mark,
    audio,
    video {
        margin: 0;
        padding: 0;
        border: 0;
        outline: 0;
        vertical-align: baseline;
        background-color: transparent !important;
        color: var(--text-color) !important;
    }

    body,
    header,
    footer,
    nav,
    menu,
    div:after,
    div:before,
    .main-menu,
    .dropdown-menu,
    .Popover,
    .tooltip,
    .navbarmain *,
    .navbar,
    .footer,
    .modal,
    .LRhc, .LRfn,
    [role="navigation"],
    [role="menu"],
    [role="treeitem"],
    [role="dialog"],
    [data-test-id="search_advanced-search-box_filter-button_filter-button-content_filter-modal"],
    [data-garden-container-id="containers.accordion"],
    #admin_content .overview .overview_content .system_updates--announcement,
    .jGrowl-notification,
    .pane.left.section.ticket-sidebar,
    .pane.right,
    [data-test-id="rich-text-editor-toolbar-container"],
    [data-test-id="rich-text-editor-toolbar-container"] div,
    [role="tooltip"],
    [data-test-id="link-tooltip-container"],
    /* ticket table header */
    .fiNJsl th,
    /* search table header */
    .cDJdQS th,
    #zd-product-tray ul,
    .zd-menu-footer,
    .popover-inner,
    [data-test-id="submit_button-button"] div,
    [data-garden-id="dropdowns.menu"],
    [data-test-id='standalone-composer-toolbar'],
    [data-test-id='table_popover'],
    [data-test-id='ticket_table_tooltip'],
    .Select-menu,
    div[data-garden-id="tooltip.tooltip"] {
        background-color: var(--base-background) !important;
    }

    a {
        color: var(--accent-color);
    }

    #branding_header {
        padding: 0;
        height: 50px;
        position: absolute;
        top: 0;
        right: 0;
        left: 60px;
        z-index: 11;
        background: transparent;
    }

    .pane.left nav,
    .pane.left nav [data-garden-id="buttons.button_group_view"],
    .bTccO {
        z-index: 1;
    }

    /* ticket save button */
    .bhZefU {
        background: rgb(47, 57, 65) !important
    }

    /* transparent backgrounds*/
    .btn,
    ul.nav-tab-list,
    .nav-tab-list li,
    .nav-tab-list div,
    .hpXXWx,
    .LRhe,
    .fsxnHQ.fsxnHQ.fsxnHQ,
    .zd-searchmenu-base,
    .LRgt,
    .LRax,
    .fGPLUj,
    .channel,
    .nav_tab button,
    #branding_header button,
    .VxCKy div,
    [data-garden-id="modals.tooltip_modal.backdrop"] {
        background-color: transparent !important;
    }

    /* selected view color*/
    .VxCKy div {
        color: var(--theme-color) !important;
    }

    /* selected ticket tab */
    .dropdown-menu li a:hover,
    ._-_-node_modules--zendesk-knowledge-components-src-Sidebar-index---listItem---1NWra._-_-node_modules--zendesk-knowledge-components-src-Sidebar-index---active---28Pqn, ._-_-node_modules--zendesk-knowledge-components-src-Sidebar-index---listItem---1NWra._-_-node_modules--zendesk-knowledge-components-src-Sidebar-index---active---28Pqn:hover,
    .btn-group.open .dropdown-toggle:not(.btn-inverse),
    [role="menuitem"]:hover,
    .Select-option.is-focused,
    .Select-option.is-selected,
    [role="tab"][data-selected="true"] {
        background-color: var(--selected-color) !important;
    }

    /* view badge counts */
    .gtrjgl {
        background-color: var(--accent-color) !important;
        border-radius: 10px;
        text-align: center;
        padding: 5px;
        vertical-align: middle;
        line-height: 0.8em;
        color: var(--main-theme-color) !important;
        font-size: 0.8rem;
        margin-left: 1rem;
    }

    .mast .profile.ticket {
        background-color: var(--accent-color) !important;
    }

    #zd-product-tray ul li,
    [data-garden-id="dropdowns.menu"] li {
        border-bottom: solid 1px #333;
    }

    .fsxnHQ.fsxnHQ.fsxnHQ,
    .btn,
    .dropdown-menu li a,
    .phone-number-select-menu .zd-menu-item > a,
    .fGPLUj,
    .zd-tag-item a,
    [data-test-id='standalone-composer-toolbar'] button,
    .event .header .actor .name a,
    .Select.has-value.Select--single > .Select-control .Select-value .Select-value-label, 
    .LRil, .LRce,
    div[data-garden-id="tooltip.tooltip"] {
        color: var(--text-color) !important;
    }

    /* internal comments */
    .rich_text .comment_input:not(.is-public) .zendesk-editor--rich-text-container,
    .rich_text .comment_input:not(.is-public) .zendesk-editor--rich-text-container *,
    .rich_text .comment_input:not(.is-public) .zendesk-editor--rich-text-container p,
    .event:not(.is-public) .comment,
    .event:not(.is-public) .zd-comment,
    .event:not(.is-public) .comment li,
    .event:not(.is-public) .zd-comment li,
    .cechoF,
    .modal .fJnKTR,
    [data-test-id='table_popover'] .LRhk {
        background-color: #fff6d9 !important;
        color: #222 !important;
    }

    /* internal comments */
    .modal .fJnKTR *,
    .event:not(.is-public) .zd-comment * {
        color: #222 !important;
    }

    /* internal links */
    .rich_text .comment_input:not(.is-public) .zendesk-editor--rich-text-container a,
    .rich_text .comment_input:not(.is-public) .zendesk-editor--rich-text-container p a,
    .event:not(.is-public) .comment a,
    .event:not(.is-public) .zd-comment a,
    .event:not(.is-public) .comment li a,
    .event:not(.is-public) .zd-comment li a,
    .cechoF a,
    .modal .fJnKTR,
    [data-test-id='table_popover'] .LRhk a, 
    .iDSXyx,
    .phone-number-select-menu .zd-menu-item > a:hover,
    .modal .fJnKTR a,
    .event:not(.is-public) .zd-comment a {
        color: var(--accent-color) !important;
    }

    /* dropdown action button on ticket */
    .dropdown-toggle:after,
    .mast .object_options_btn:after {
        filter: invert(70%)
    }

    /* ticket table title links */
    .jydokf,
    table a,
    table a span, 
    .fOdaZx button,
    [role="treeitem"] {
        color: var(--accent-color) !important;
    }

    .fOdaZx button,
    [data-test-id="talk-ticket-composer-outbound-call-menu"]
    { 
      border: none;
    }

    /* All dialogs */
    [role="dialog"],
    /* Bulk action button modal */
    [data-test-id="bulk-actions"] {
        background-color: var(--base-background) !important;
        padding: 1em;
        border-radius: 5px;
        border: solid var(--text-color) 1px;
    }

    /* set bulk action z-index so it doesn't fall behind the left sidebar when it is scrolling */
    [data-test-id="bulk-actions"] {
      z-index: 10;
    }

    /* Bulk action buttons */
    [data-test-id="bulk-actions"] [data-garden-id="buttons.button_group_view"],
    /* Link tooltip editing container */
    [data-test-id="link-tooltip-container"],
    [data-garden-id="buttons.button_group_view"],
    .c-btn--primary {
        border-radius: 5px;
        border: solid var(--accent-color) 1px !important;
    }

    header,
    table tr,
    table td,
    [role="tab"],
    [data-test-id="header-toolbar"],
    .activities .item,
    .indicators ul,
    .indicators li,
    [data-test-id="ticket-fields"] input,
    [aria-haspopup="listbox"],
    .zd-tag-menu-root,
    [data-garden-id^="dropdowns"],
    [data-test-id="ticket-pane-subject"],
    [data-garden-id="buttons.button"],
    [data-test-id="ticket-footer-post-save-actions-menu-button"],
    .btn,
    .zd-tag-item,
    .section.ticket .tab-controls-container,
    .event,
    .property_box {
        border-color: var(--border-color) !important;
    }

    [data-garden-id="tabs.tabs"] [aria-selected="true"]
    {
      border-bottom: solid var(--accent-color) 2px !important;
    }

    a[role="treeitem"]:hover span {
        filter: brightness(250%);
    }

    /* badges */
    [data-test-id="status-badge-closed"] {
        color: var(--theme-color) !important;
        background-color: var(--closed-background) !important;
    }

    [data-test-id="status-badge-open"] {
        background-color: var(--open-background) !important;
    }

    [data-test-id="status-badge-new"] {
        background-color: var(--new-background) !important;
    }

    [data-test-id="status-badge-pending"] {
        background-color: var(--pending-background) !important;
    }

    [data-test-id="status-badge-solved"] {
        background-color: var(--solved-background) !important;
    }

    [data-garden-id="modals.backdrop"],
    /* modal login transparency */
    .u-bg-transparent {
        background-color: rgba(0, 0, 0, 0.8) !important;
    }

    .u-bg-transparent body {
        background-color: transparent !important;
    }
}

/* ==UserStyle==
@name           Google Dark
