  {
    padding-right: 25px;
    background: no-repeat right center;
    background-size: 20px 20px;
  }

  a[href*='abnormalreturns.com']
  {
    background-image: url("data:image/png;base64,");    
  }

  a[href*='fortune.com']
  {
    background-image: url("data:image/x-icon;base64,");
  }

  a[href*='wsj.com']
