/* ==UserStyle==
@name           Ring Dark
@namespace      https://github.com/Brandan/UserStyles/raw/main/RingDark.user.css
@author         Brandan E. Lloyd
@description    `Modifications to Ring's style to enable a dark mode`
@version        20231003
@license        GPL
==/UserStyle== */

@-moz-document domain("ring.com")
{
  img[src*="cloudfront.net/icons/"]
  {
    filter:invert(1) !important;
  }

  .eMjRjo,
  .geNAPp,
  .jIkExR,
  .jFiLcK,
  .yXiAQ,
  [role='alertdialog'],
  [data-sticky='true'],
  [class*='styled__TooltipBox'],
  [class*='styled__List'],
  [class*='styled__BackButton'],
  [class*='styled__FullScreenContainer'],
  [class*='styled__Container']
  {
    background-color: var(--theme-color) !important;
  }

  .NVQIG
  {
    background-color: rgba(21, 21, 31, 0.97) !important
  }

  [class*='styled__Backdrop']
  {
    background-color: rgba(0,0,0,0.8) !important
  }

  .busAsX
  {
    background-color: var(--accent-color) !important;
  }

  .iIVGIZ, 
  .iAYRqk,
  .jIkExR,
  .kNPZlK,
  button
  {
    border: solid var(--text-color) 1px !important;
  }

  button[class*='styled__DeviceSettingsButton']
  {
    border: none !important;
  }

  [disabled]
  {
    opacity: 0.5;
  }

  [class*='styled__LocationName']
  {
    overflow: unset !important;
  }

  [class*='styled__Shortcuts'],   
  [class*='styled__TilesAnchor'],
  [class*='consent-banner']
  {
    display: none;
  }

  /* Selected Mode */
  .eBVDyM
  {
    background: var(--negativeBase) !important;
  }

  .modal-backdrop, .action-banner-backdrop {
    background-color: rgba(0, 0, 0, 0.65) !important;
  }

  .modal-container {
    background-color: var(--theme-color) !important;
  }
}

/* ==UserStyle==
@name           Audible Dark
@namespace      https://github.com/Brandan/UserStyles/raw/main/AudibleDark.user.css
@author         Brandan E. Lloyd
@description    `Modifications to Audible's style to enable a dark mode`
@version        20220812.3
@license        GPL
==/UserStyle== */

@-moz-document domain("audible.com")
{
    ::placeholder {
        color: var(--placeholder-color) !important;    
    }

    .bc-input,
    select, 
    option {
        background-color: var(--theme-color) !important;        
    }

    .bc-color-base, 
    .bc-dropdown-color
    {
        color: var(--text-color) !important;    
    }

    .bc-input-box-color,
    .bc-color-border-base,
    .bc-chip.bc-chip-outline .bc-chip-border
    {
        border-color: var(--text-color) !important;    
    }

    .bc-color-error
    {
        color: #e00 !important;    
    }

    /* progress bar fill  */
    .bc-color-background-progress {
        background-color: var(--accent-color) !important;
    }

    /* button colors */

    .bc-button-active {
          background-color: var(--accent-color) !important;    
    }

    .bc-button-primary, .bc-button-primary a * {
      background-color: var(--link-color) !important;
      color: var(--text-color) !important;
    }

    .bc-button-secondary {
        background-color: var(--accent-color) !important;
    }

    .bc-button-preorder {
        background-color: purple !important;
    }

    /* icon partial invert */
    .bc-popover-beak {
        filter:invert(0.8) !important;
    }

    /* icon inverts */
    .library-button-row-icon {
        filter:invert(1) !important;
    }

    /* Ratings */
    .bc-icon-fill-progress, 
    .bc-icon-fill-base {
        background-color: var(--accent-color) !important;
    }

    /* icons reset */
    .bc-icon.bc-icon-cart,
    .bc-icon.bc-icon-caret-down,
    .bc-accordion .bc-accordion-icon-position-right .bc-icon,
    .bc-icon.bc-icon-chevron-down,
    .bc-icon.bc-icon-chevron-left,
    .bc-icon.bc-icon-chevron-right,
    .bc-icon.bc-icon-chevron-up,
    .bc-icon.bc-icon-facebook-square,
    .bc-icon.bc-icon-gift,
    .bc-icon.bc-icon-move-to-bottom,
    .bc-icon.bc-icon-twitter {
        background-color: transparent !important;
    }

    /* Added styling for the popover container */
    .bc-popover-inner
    {
        border-color: var(--text-color) !important;
        box-shadow: 0 2px 5px 0 var(--accent-color) !important;
    }

    .slick-arrow .arrows
    {
        background-color: #e3e3e3 !important
    }
}

